pragma solidity ^0.8.0;

contract sid_e6317f9e_4d23_4a94_bc7b_f1e509cf531a {
    uint public tokenState;
    address[6] public participants;
    mapping(uint => uint) public taskToId;
    mapping(uint => uint) public participantToIndex;
    uint public conditions;

    constructor(address[6] memory _participants) {
        participants = _participants;
        participantToIndex[sid_7DBFC1E2_3F1E_42FB_99DF_C2F204BE2E90] = 0;
        participantToIndex[sid_EFC01FD5_5D35_4863_92FF_B445358BCE4F] = 1;
        participantToIndex[sid_4519EFA1_14A7_4803_98EA_58849BC4EE52] = 2;
        participantToIndex[sid_9F4E5B86_AD88_481D_9D83_31F4B0C8C266] = 3;
        participantToIndex[sid_AAE00E03_B197_4DB9_91A0_488B36456775] = 4;
        participantToIndex[sid_8CC6BB10_BD67_40CF_B287_76D0B24E0058] = 5;

        taskToId[sid_E70C051E_E333_4C36_84E7_4EFC594CA4F9] = 1;
        taskToId[sid_5E7F8B9A_D08B_4075_A5AA_334D33017824] = 2;
        taskToId[sid_14F93670_7F19_4E8D_985B_652CC65D24D6] = 3;

        tokenState = 1; // Start at the first task
    }

    function setconditions(uint _conditions) public {
        conditions = _conditions;
    }

    function enact(uint taskID) public {
        require(tokenState == taskID, "Invalid task ID for current state");
        uint participantIndex = getParticipantIndex(taskID);
        require(msg.sender == participants[participantIndex], "Only the initiating participant can execute this task");

        if (taskID == 1) {
            tokenState = 2;
        } else if (taskID == 2) {
            tokenState = 3;
        } else if (taskID == 3) {
            tokenState = 0; // End of process
        } else {
            revert("Invalid task ID");
        }
    }

    function getParticipantIndex(uint taskID) private view returns (uint) {
        if (taskID == 1) {
            return participantToIndex[sid_7DBFC1E2_3F1E_42FB_99DF_C2F204BE2E90]; // Guest
        } else if (taskID == 2) {
            return participantToIndex[sid_4519EFA1_14A7_4803_98EA_58849BC4EE52]; // ARS
        } else if (taskID == 3) {
            return participantToIndex[sid_AAE00E03_B197_4DB9_91A0_488B36456775]; // Host
        } else {
            revert("Task ID does not have an associated participant");
        }
    }
}
