//SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

contract ChoreographyExample2 {
  uint public tokenState = 1;
  address[3] public participants;
  uint public conditions;

  constructor(address[3] memory _participants) {
    participants = _participants;
  }

  function setConditions(uint _conditions) external {
    conditions = _conditions;
  }

  function enact(uint id) external {
    uint _tokenState = tokenState;

    while (_tokenState != 0) {
      if (_tokenState & 1 == 1) {
        // <--- ChoreographyTask_1vnykqp Order goods --->
        if (id == 0 && msg.sender == participants[0]) {
          _tokenState &= ~uint(1);
          _tokenState |= 6;
          continue;
        }
      }

      if (_tokenState & 6 == 6) {
        if ((conditions & 1) == 1) {
          // Transition to Confirm order path
          _tokenState &= ~uint(6);
          _tokenState |= 32;
          continue;
        } else {
          // Default branch: unavailable
          _tokenState &= ~uint(6);
          _tokenState |= 24;
          continue;
        }
      }

      if (_tokenState & 24 == 24) {
        // Parallel Gateway: activate both Produce goods and Inform customer
        _tokenState &= ~uint(24);
        _tokenState |= 2 | 4;
        continue;
      }

      if (_tokenState & 2 == 2) {
        // <--- ChoreographyTask_16lc74v Produce goods --->
        if (id == 1 && msg.sender == participants[1]) {
          _tokenState &= ~uint(2);
          _tokenState |= 8;
          continue;
        }
      }

      if (_tokenState & 4 == 4) {
        // <--- ChoreographyTask_07t2zjo Inform customer --->
        if (id == 2 && msg.sender == participants[1]) {
          _tokenState &= ~uint(4);
          _tokenState |= 16;
          continue;
        }
      }

      if (_tokenState & 8 == 8 && _tokenState & 16 == 16) {
        // Both branches completed, join at parallel gateway
        _tokenState &= ~uint(24); // Clear both bits
        _tokenState |= 64;
        continue;
      }

      if (_tokenState & 64 == 64) {
        // Forward to final gateway
        _tokenState &= ~uint(64);
        _tokenState |= 128;
        continue;
      }

      if (_tokenState & 32 == 32) {
        // <--- ChoreographyTask_0n57gae Confirm order --->
        if (id == 4 && msg.sender == participants[0]) {
          _tokenState &= ~uint(32);
          _tokenState |= 128;
          continue;
        }
      }

      if (_tokenState & 128 == 128) {
        // Final gateway joins to Ship goods
        _tokenState &= ~uint(128);
        _tokenState |= 256;
        continue;
      }

      if (_tokenState & 256 == 256) {
        // <--- ChoreographyTask_056ylqg Ship goods --->
        if (id == 3 && msg.sender == participants[1]) {
          _tokenState &= ~uint(256);
          break; // End of process
        }
      }

      break;
    }

    tokenState = _tokenState;
  }
}
